        public Deliverable(string courseCode, string task, DateTime dueDate, string note)
        {
            CourseCode = courseCode;
            Task = task;
            DueDate = dueDate.Date;
            AdditionalNotes = note;
        }
    }

    public void DisplayForm()
    {
        ModalDisplay = "block;";
        ModalClass = "Show";
