        StateHasChanged();
    }

    private void AddDeliverable()
    {
        //adding deliverable to list
        deliverables.Add(newDeliverable = new Deliverable(newCoursecode, newTask, newDueDate, newNote));

        ResetForm();

        //reloading page
        Close();
    }

    private void ResetForm()
    {
